package javaproject;

import java.util.Scanner;

public class Alldatatypeinclass {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc= new Scanner(System.in);
		
		System.out.println("enter a float,double,boolean,long");
		
		float a=sc.nextFloat();
		double b=sc.nextDouble();
		boolean c=sc.nextBoolean();
		long d=sc.nextLong();
		
		System.out.println("enetered float is: "+a);
		System.out.println("enetered double is: "+b);
		System.out.println("enetered boolean is: "+c);
		System.out.println("enetered long is: "+d);
		
				
		
		
		

	}

}
